import React from 'react';
import CalendarEvent from './CalendarEvent';
import './CalendarContainer.css';
import HourLine from './HourLine';
import { useState } from 'react';
import AddEventForm from './AddEventForm';

function CalendarContainer({events}) {

  const [ev, setEv] = useState([]);

  // Add event to dailyEvents array
  const handleAddEvent = (newEvent) => {
    setEv([...ev, newEvent]);
  };

  // Function to generate CalendarEvent components based on dailyEvents data
  const renderEvents = () => {
    return dailyEvents.map((event, index) => (
      <div
        key={index}
        className="EventWrapper"
        style={{ top: calculateEventPosition(event.startTime) }}
      >
        <CalendarEvent
          startTime={event.startTime}
          endTime={event.endTime}
          title={event.title}
          location={event.location}
          eventType={event.eventType}
        />
      </div>
    ));
  };

  // Example data for daily events
  const dailyEvents = [
    {
      startTime: new Date(2024, 3, 27, 9, 0), // Year, Month (0-indexed), Day, Hour, Minute
      endTime: new Date(2024, 3, 27, 10, 30),
      title: 'Team Meeting',
      location: 'Conference Room A',
      eventType: 'meeting'
    },
    {
      startTime: new Date(2024, 3, 27, 12, 0),
      endTime: new Date(2024, 3, 27, 13, 0),
      title: 'Lunch with Friends',
      location: 'Restaurant Name',
      eventType: 'party'
    },
    {
      startTime: new Date(2024, 3, 27, 14, 0),
      endTime: new Date(2024, 3, 27, 16, 0),
      title: 'Workshop: React Basics',
      location: 'Online',
      eventType: 'workshop'
    }
    // Add more events as needed
  ];

  // Function to generate hour slots between 6:00 AM and 11:00 PM
  const renderHourSlots = () => {
    const hours = [];
    for (let i = 6; i <= 23; i++) {
      hours.push(
        <div key={i} className="HourSlot">
          {i % 2 === 0 ? (
            <div className="HourLabel">
                {i % 12 || 12} {i < 12 ? 'AM' : 'PM'}
                <div className='DashedLine'/>
            </div>
          ) : <div className='HourLine'/> }
        </div>
      );
    }
    return hours;
  };

  // Function to render hour lines in the EventsColumn, one for every hour between 6:00 AM and 11:00 PM, spaced evenly
  const renderEventColumnHourLines = () => {
    const hourLines = [];
    for (let i = 6; i <= 23; i++) {
      hourLines.push(
        <div
          key={i}
          className="EventColumnHourLine"
          style={{ top: `${((i - 6) * 60) + 20}px` }} // Convert hours to pixels
        />
      );
    }
    return hourLines;
  }

  // Function to generate HourLine components within the CalendarContainer for every hour between 6:00 AM and 11:00 PM
    const renderHourLines = () => {
        const hourLines = [];
        for (let i = 6; i <= 23; i++) {
        hourLines.push(
            <HourLine
            key={i}
            top={`${((i - 6) * 20) + 20}px`} // Convert hours to pixels
            />
        );
        }
        return hourLines;
    };

  // Calculate the position of each event based on its start time
  const calculateEventPosition = (startTime) => {
    const startHour = startTime.getHours();
    console.log(startHour);
    const startMinute = startTime.getMinutes();
    const totalMinutes = startHour * 60 + startMinute - 6 * 60; // Subtract 6 hours to start at 6:00 AM
    return `${((totalMinutes / 60) * 30) + 20}px`; // Convert minutes to pixels
  };

  return (
    <div className="CalendarContainer">
      <div className="HourColumn">
        {/* Render hour slots */}
        {renderHourSlots()}
      </div>
      <div className="EventsColumn">
        {renderEvents()}
      </div>
      <div>
        <AddEventForm onAddEvent={handleAddEvent} />
      </div>
    </div>
  );
}

export default CalendarContainer;
