import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, useRoutes } from 'react-router-dom';
import CalendarContainer from './CalendarContainer';
import AddEventForm from './AddEventForm';
import MiddleContainer from './MiddleContainer';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import HomePage from './HomePage';
import { doCreateUserWithEmailAndPassword, doSignInWithEmailAndPassword } from './firebase/auth';
import './fonts.css';

function App() {
  const [events, setEvents] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const routesArray = [
    {
      path: "*",
      element: <LoginPage />,
    },
    {
      path: "/login",
      element: <LoginPage />,
    },
    {
      path: "/signup",
      element: <SignupPage />,
    },
    {
      path: "/home",
      element: <HomePage />,
    },
  ];

  const handleAddEvent = (newEvent) => {
    setEvents([...events, newEvent]);
  };

  const handleSignUp = async () => {
    try {
      await doCreateUserWithEmailAndPassword(email, password);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleSignIn = async () => {
    try {
      await doSignInWithEmailAndPassword(email, password);
    } catch (error) {
      setError(error.message);
    }
  };

  const getTodayDate = () => {
    const today = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return today.toLocaleDateString('en-US', options);
  };

  return (
    <div className="App">
      <header className="Header">
        <h1 className='h3'>PlanMate</h1>
        <nav>
          <ul className='h4'>
            <li><a href="#home">Home</a></li>
            <li><a href="#events">Events</a></li>
            <li><a href="#create">Create Event</a></li>
            <li><a href="#profile">Profile</a></li>
          </ul>
        </nav>
      </header>
      <main className="MainContent">
      <section id="signup-login">
          <h2 className='h2'>Sign Up / Login</h2>
          <div>
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button onClick={handleSignUp}>Sign Up</button>
            <button onClick={handleSignIn}>Login</button>
            {error && <p>{error}</p>}
          </div>
        </section>
        <section id="create">
          <h2 className='h2'>Create Event</h2>
          <MiddleContainer />
        </section>
        <section id="events">
          <h2 className='h2'>
            <i className="material-icons">chevron_left</i>
            {getTodayDate()}
            <i className="material-icons">chevron_right</i>
          </h2>
          <CalendarContainer events={events} />
        </section>
      </main>
      <footer className="Footer">
        <p>&copy; 2024 PlanMate</p>
      </footer>
    </div>
  );
}

export default App;
